import React, { Component } from "react";
import {
    Container, Row, Col, Card, CardImg, CardText, CardBody,
    CardTitle, Button
} from 'reactstrap';
import {connect} from 'react-redux';
import * as actions from './../../actions/index';
import Control from './../../components/Control/Control';

class ProductListShowContainer extends Component {
    constructor(props) {
        super(props);
        this.state = {
            filterName: '',
            filterStatus: -1
        }
    }

    componentDidMount() {
        this.props.fetchAllItems();
    }

    render() {
        var {tasks, filterTable, keyword, sort} = this.props; //truy xuất được các thành phần trong product thông qua biến state

        //search
        tasks = tasks.filter((task) => {
            return task.name.toLowerCase().indexOf(keyword.toLowerCase()) !== -1
        });

        //sort 
        if(sort.by === 'name') {
            tasks.sort((a, b) => {
                if(a.name > b.name) return sort.value;
                else if (a.name < b.name) return -sort.value;
                else return 0;
            });
        } else {
            tasks.sort((a, b) => {
                if(a.status > b.status) return -sort.value;
                else if (a.status < b.status) return sort.value;
                else return 0;
            })
        }

        var elmTasks = tasks.map((task, index) => {
            return <Col sm="4">
                <Card>
                    <CardImg top width="100%"
                        src={task.imageUrl}
                        alt="Card image cap" />
                    <CardBody>
                        <CardTitle>{task.name}</CardTitle>
                        <CardText>{task.price} $</CardText>
                        {task.status ? <Button> Thêm vào giỏ </Button> : <CardTitle>Hết hàng</CardTitle>}
                    </CardBody>
                </Card>
            </Col>
        });

        return (
            <Container>
                <h2>Products</h2>
                <Control />
                <hr />
                <Row>
                    {tasks.map(task => (
                        <Col sm="4">
                            <Card>
                                <CardImg top width="100%"
                                    src={task.imageUrl}
                                    alt="Card image cap" />
                                <CardBody>
                                    <CardTitle>{task.name}</CardTitle>
                                    <CardText>{task.price} $</CardText>
                                    {task.status ? <Button> Thêm vào giỏ </Button> : <CardTitle>Hết hàng</CardTitle>}
                                </CardBody>
                            </Card>
                        </Col>
                    ))}
                </Row>
            </Container>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        tasks : state.tasks,
        filterTable : state.filterTable,
        keyword : state.search,
        sort : state.sort
    }
}

const mapDispatchToProps = (dispatch, props) => {
    return {
        fetchAllItems : () => {
            dispatch(actions.listAllRequest());
        },
        onFilterTable : (filter) => {
            dispatch(actions.filterItem(filter));
        }
    }
}

export default  connect(mapStateToProps, mapDispatchToProps)(ProductListShowContainer);
