import React, { Component } from "react";
import {
    Container, Row, Col, Card, CardImg, CardText, CardBody,
    CardTitle, Button
} from 'reactstrap';
import {connect} from 'react-redux';
import * as actions from './../../actions/index';
import ControlSearch from './../../components/Control/ControlSearch';
import './ProductListShowContainer.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCartPlus } from '@fortawesome/free-solid-svg-icons';

class ProductListShowContainer extends Component {
    constructor(props) {
        super(props);
        this.state = {
            filterName: '',
            filterStatus: -1
        }
    }

    componentDidMount() {
        this.props.fetchAllItems();
    }

    render() {
        var {tasks, keyword} = this.props; //truy xuất được các thành phần trong product thông qua biến state

        //search
        tasks = tasks.filter((task) => {
            return task.name.toLowerCase().indexOf(keyword.toLowerCase()) !== -1
        });

        var elmTasks = tasks.map((task, index) => {
            return <Col sm="4">
                <Card>
                    <CardImg top width="100%"
                        src={task.imageUrl}
                        alt="Card image cap" />
                    <CardBody>
                        <CardTitle>{task.name}</CardTitle>
                        <CardText>{task.price} $</CardText>
                        {task.status ? <Button color="success" > Thêm vào giỏ <FontAwesomeIcon icon={faCartPlus} /> </Button> : <CardTitle className="text-white">Hết hàng</CardTitle>}
                    </CardBody>
                </Card>
            </Col>
        });

        return (
            <Container>
                <Row className = "mtop-15">
                    <h2>Danh sách sản phẩm</h2>
                    <ControlSearch />
                </Row>
                <hr />
                <Row>
                    {elmTasks}
                </Row>
            </Container>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        tasks : state.tasks,
        filterTable : state.filterTable,
        keyword : state.search,
        sort : state.sort
    }
}

const mapDispatchToProps = (dispatch, props) => {
    return {
        fetchAllItems : () => {
            dispatch(actions.listAllRequest());
        },
        onFilterTable : (filter) => {
            dispatch(actions.filterItem(filter));
        }
    }
}

export default  connect(mapStateToProps, mapDispatchToProps)(ProductListShowContainer);
