import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col } from 'reactstrap';
import './WorkManagement.css';
import { Button } from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlusSquare } from '@fortawesome/free-solid-svg-icons';
import TaskForm from './components/TaskForm';
import Control from './components/Control';
import TaskList from './components/TaskList';


import axios from 'axios';
const uuidv4 = require("uuid/v4");

const isEmpty = v => {
    return Object.keys(v).length === 0;
  };

class WorkManagement extends Component {

    constructor(props) {
        super(props);
        this.state = {
            item : {},
            isDisplayForm: true
        }
        this.onChangeDisplay = this.onChangeDisplay.bind(this);
        //this.componentDidMount = this.componentDidMount.bind(this);
    }

    componentDidUpdate() {
        if (!isEmpty(this.state.item)) {
            axios.post('http://localhost:3001/workList', this.state.item).then(function (response) {
                console.log(response);
            })
                .catch(function (error) {
                    console.log(error);
                })
        }
    }

    onChangeDisplay() {
        this.state.isDisplayForm === true ? this.setState ({
            isDisplayForm : false
        }) : (this.setState ({
            isDisplayForm : true
        }))
        this.componentDidUpdate();
    }

    // onSubmit = (name, status) => {
    //     var id = uuidv4();
    //     var item = {
    //         id : id,
    //         name : name,
    //         status : status 
    //     }
    //     this.setState({
    //         item : item,
    //         tasks : this.state.tasks.concat(item)
    //     })
    // }

    render() {
        const {isDisplayForm} = this.state;
        //console.log(this.state.item);
        var elmTaskFrom = isDisplayForm ? <TaskForm onChangeDisplay={this.onChangeDisplay} isDisplayForm={isDisplayForm} /> : '';
        var elmTaskCreate = isDisplayForm ? '' : <Button onClick={this.onChangeDisplay} color="primary" size="lg">
        <FontAwesomeIcon icon={faPlusSquare} /> &nbsp;
        Thêm công việc
        </Button> ;
        //console.log(this.state.isDisplayForm);
        return (
            <Container>
                <div className="text-center">
                    <h1>Quản lý công việc</h1>
                    <hr />
                </div>
                <Row>
                    {elmTaskFrom}
                    <Col>
                        {elmTaskCreate}
                        <Control />
                        <TaskList/>
                    </Col>
                </Row>
            </Container>
        )
    }

}



export default WorkManagement;
