import React, { Component } from "react";
import {
    Container, Row, Col, Card, CardImg, CardText, CardBody,
    CardTitle, Button
} from 'reactstrap';
import {connect} from 'react-redux';
import * as actions from './../../actions/index';

class ProductListShowContainer extends Component {
    constructor(props) {
        super(props);
        this.state = {
            filterName: '',
            filterStatus: -1
        }
    }

    componentDidMount() {
        this.props.fetchAllItems();
    }

    render() {
        var {tasks, filterTable, keyword, sort} = this.props; //truy xuất được các thành phần trong product thông qua biến state
        return (
            <Container>
                <h2>Products</h2>
                <Row>
                    {tasks.map(task => (
                        <Col sm="4">
                            <Card>
                                <CardImg top width="100%"
                                    src={task.imageUrl}
                                    alt="Card image cap" />
                                <CardBody>
                                    <CardTitle>{task.name}</CardTitle>
                                    <CardText>{task.status.value}</CardText>
                                    <Button>
                                        Thêm vào giỏ
                                            </Button>
                                </CardBody>
                            </Card>
                        </Col>
                    ))}
                </Row>
            </Container>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        tasks : state.tasks,
        filterTable : state.filterTable,
        keyword : state.search,
        sort : state.sort
    }
}

const mapDispatchToProps = (dispatch, props) => {
    return {
        fetchAllItems : () => {
            dispatch(actions.listAllRequest());
        },
        onFilterTable : (filter) => {
            dispatch(actions.filterItem(filter));
        }
    }
}

export default  connect(mapStateToProps, mapDispatchToProps)(ProductListShowContainer);
