import React, { Component } from "react";
import {
    Container, Row, Col, Card, CardImg, CardText, CardBody,
    CardTitle, Button
} from 'reactstrap';

class Products extends Component {
    constructor(props) {
        super(props);
        this.state = {
            products: [
                {
                    "id": "1",
                    "name": "product.111",
                    "description": "this is product.111, you see",
                    "imageUrl": "https://images.pexels.com/photos/20787/pexels-photo.jpg?auto=compress&cs=tinysrgb&h=350"
                },
                {
                    "id": "2",
                    "name": "product.2",
                    "description": "this is product.222, you see",
                    "imageUrl": "https://images.pexels.com/photos/20787/pexels-photo.jpg?auto=compress&cs=tinysrgb&h=350"
                },
                {
                    "id": "3",
                    "name": "product.333",
                    "description": "this is product.333, you see",
                    "imageUrl": "https://images.pexels.com/photos/20787/pexels-photo.jpg?auto=compress&cs=tinysrgb&h=350"
                },
                {
                    "id": "4",
                    "name": "product.4",
                    "description": "this is product.444, you see",
                    "imageUrl": "https://images.pexels.com/photos/20787/pexels-photo.jpg?auto=compress&cs=tinysrgb&h=350"
                },
                {
                    "id": "4",
                    "name": "product.4",
                    "description": "this is product.444, you see",
                    "imageUrl": "https://images.pexels.com/photos/20787/pexels-photo.jpg?auto=compress&cs=tinysrgb&h=350"
                },
                {
                    "id": "4",
                    "name": "product.4",
                    "description": "this is product.444, you see",
                    "imageUrl": "https://images.pexels.com/photos/20787/pexels-photo.jpg?auto=compress&cs=tinysrgb&h=350"
                },
                {
                    "id": "4",
                    "name": "product.4",
                    "description": "this is product.444, you see",
                    "imageUrl": "https://images.pexels.com/photos/20787/pexels-photo.jpg?auto=compress&cs=tinysrgb&h=350"
                },
                {
                    "id": "4",
                    "name": "product.4",
                    "description": "this is product.444, you see",
                    "imageUrl": "https://images.pexels.com/photos/20787/pexels-photo.jpg?auto=compress&cs=tinysrgb&h=350"
                },
                {
                    "id": "4",
                    "name": "product.4",
                    "description": "this is product.444, you see",
                    "imageUrl": "https://images.pexels.com/photos/20787/pexels-photo.jpg?auto=compress&cs=tinysrgb&h=350"
                }
            ]
        }
    }
    render() {
        const { products } = this.state; //truy xuất được các thành phần trong product thông qua biến state
        return (
            <Container>
                <h2>Products</h2>
                <Row>
                    {products.map(product => (
                        <Col sm="4">
                            <Card>
                                <CardImg top width="100%"
                                    src={product.imageUrl}
                                    alt="Card image cap" />
                                <CardBody>
                                    <CardTitle>{product.name}</CardTitle>
                                    <CardText>{product.description}</CardText>
                                    <Button>Add to cart</Button>
                                </CardBody>
                            </Card>
                        </Col>
                    ))}
                </Row>
            </Container>
        )
    }
}

export default Products;
