import React, { Component } from "react";
import {
    Container, Row, Col, Card, CardImg, CardText, CardBody,
    CardTitle, Button
} from 'reactstrap';
import {connect} from 'react-redux';
//import * as actions from './../../actions/index';


class ProductCartContainer extends Component {
    
    render() {
        var {carts} = this.props;
        console.log(carts);

        var elmTasks = carts.map((cart, index) => {
            return  <Col sm="4">
                <Card>
                    <CardImg top width="100%"
                        src={cart.imageUrl}
                        alt="Card image cap" />
                    <CardBody>
                        <CardTitle>{cart.name}</CardTitle>
                        <CardText>{cart.price} $</CardText>
                        {cart.status ? <Button color="danger" > Xóa khỏi giỏ hàng </Button> : <CardTitle>Hết hàng</CardTitle>}
                    </CardBody>
                </Card>
            </Col>
        });

        return (
            <Container>
                <Row className = "mtop-15">
                    <Col>
                        <h2>Giỏ hàng (0 sản phẩm)</h2>
                        <h3>Thành tiền : 500 $</h3>
                    </Col>
                    <Col>
                        <Button color="primary">Tiến hành đặt hàng</Button> &nbsp;
                        <Button color="danger">Xóa tất cả</Button>
                    </Col>
                </Row>
                <hr />
                <Row>
                    {elmTasks}
                </Row>
            </Container>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        carts : state.carts,
    }
}

const mapDispatchToProps = (dispatch, props) => {
    return {
    }
}

export default  connect(mapStateToProps, mapDispatchToProps)(ProductCartContainer);
