import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col } from 'reactstrap';
import './WorkManagement.css';
import { Button} from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlusSquare } from '@fortawesome/free-solid-svg-icons';
import TaskForm from '../WorkManagement/components/TaskForm';
import Control from '../WorkManagement/components/Control';
import TaskList from '../WorkManagement/components/TaskList';
import axios from 'axios';

class WorkManagement extends Component {

    constructor(props) {
        super(props);
        this.state = {
            tasks : [],
            isDisplayForm : true
        }
        this.onChangeDisplay = this.onChangeDisplay.bind(this);
        //this.componentDidMount = this.componentDidMount.bind(this);
    }

    componentDidMount(){
        axios.get('http://localhost:3001/workList').then(res => {
            this.setState({
                tasks: res.data
            });
        })
    }

    onChangeDisplay() {
        // this.state.isDisplayForm === true ? this.setState ({
        //     isDisplayForm : false
        // }) : (this.setState ({
        //     isDisplayForm : true
        // }))
        this.setState ({
            isDisplayForm : false
        })

        
    }

    loss() {
        console.log("cái đuỵt");
    }

    render() {
        const {tasks, isDisplayForm} = this.state;
        var elmTaskFrom =  isDisplayForm ? <TaskForm onChangeDisplay = {this.onChangeDisplay} isDisplayForm = {isDisplayForm} /> : '';
        var elmTaskCreate = isDisplayForm ? '' : <Button onClick={this.loss} color="primary" size="lg" disabled>
        <FontAwesomeIcon icon={faPlusSquare} /> &nbsp;
        Thêm công việc
        </Button> ;
        console.log(this.state.isDisplayForm);
        return (
            <Container>
                <div className="text-center">
                    <h1>Quản lý công việc</h1>
                    <hr />
                </div>
                <Row>
                    {elmTaskFrom}
                    <Col>
                    <Button onClick={this.loss} color="primary" size="lg" disabled>
        <FontAwesomeIcon icon={faPlusSquare} /> &nbsp;
        Thêm công việc
        </Button>
                        <Control />
                        <TaskList tasks = {tasks}/>
                    </Col>
                </Row>
            </Container>
        )
    }

}

export default WorkManagement;
