import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col } from 'reactstrap';
import './WorkManagement.css';
import { Button } from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlusSquare } from '@fortawesome/free-solid-svg-icons';
import TaskForm from './components/TaskForm';
import Control from './components/Control';
import TaskList from './components/TaskList';


// const isEmpty = v => {
//     return Object.keys(v).length === 0;
//   };

class WorkManagement extends Component {

    constructor(props) {
        super(props);
        this.state = {
            isDisplayForm: true
        }
        this.onChangeDisplay = this.onChangeDisplay.bind(this);
    }

    onChangeDisplay() {
        this.state.isDisplayForm === true ? this.setState ({
            isDisplayForm : false
        }) : (this.setState ({
            isDisplayForm : true
        }))
    }


    render() {
        const {isDisplayForm} = this.state;
        var elmTaskFrom = isDisplayForm ? <TaskForm onChangeDisplay={this.onChangeDisplay} isDisplayForm={isDisplayForm} /> : '';
        var elmTaskCreate = isDisplayForm ? '' : <Button onClick={this.onChangeDisplay} color="primary" size="lg">
        <FontAwesomeIcon icon={faPlusSquare} /> &nbsp;
        Thêm công việc
        </Button> ;
        return (
            <Container>
                <div className="text-center">
                    <h1>Quản lý công việc</h1>
                    <hr />
                </div>
                <Row>
                    {elmTaskFrom}
                    <Col>
                        {elmTaskCreate}
                        <Control />
                        <TaskList/>
                    </Col>
                </Row>
            </Container>
        )
    }

}



export default WorkManagement;
