import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Col } from 'reactstrap';
import './WorkManagement.css';
import { UncontrolledDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
import {connect} from 'react-redux';
import * as actions from './../../../actions/index';


class ControlSort extends Component{

    onClick = (sortBy, sortValue) => {
        this.props.onSort({
            by : sortBy,
            value : sortValue
        });
    }

    render() {
        //console.log(this.props.sort);
        return (
            <Col xs="auto">
                <UncontrolledDropdown>
                    <DropdownToggle color="primary" caret>
                        Sắp xếp
                                </DropdownToggle>
                    <DropdownMenu>
                        <DropdownItem onClick = {() => this.onClick('name', 1)}>
                            <a 
                                role = "button"
                                className = {(this.props.sort.by === 'name' && this.props.sort.value === 1) ? 'sort_selected' : ''}
                            >
                                Tên từ A-Z
                            </a>
                        </DropdownItem>
                        <DropdownItem onClick={() => this.onClick('name', -1)}>
                            <a
                                role="button"
                                className={(this.props.sort.by === 'name' && this.props.sort.value === -1) ? 'sort_selected' : ''}
                            >
                                Tên từ Z-A
                            </a>
                        </DropdownItem>
                        <DropdownItem divider />
                        <DropdownItem onClick={() => this.onClick('status', 1)}>
                            <a
                                role="button"
                                className={(this.props.sort.by === 'status' && this.props.sort.value === 1) ? 'sort_selected' : ''}
                            >
                                Trạng thái kích hoạt
                            </a>
                        </DropdownItem>
                        <DropdownItem onClick={() => this.onClick('status', -1)}>
                            <a
                                role="button"
                                className={(this.props.sort.by === 'status' && this.props.sort.value === -1) ? 'sort_selected' : ''}
                            >
                                Trạng thái ẩn
                            </a>
                        </DropdownItem>
                    </DropdownMenu>
                </UncontrolledDropdown>
            </Col>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        sort : state.sort
    }
}

const mapDispatchToProps = (dispatch, props) => {
    return{
        onSort : (sort) => {
            dispatch(actions.sortItem(sort));
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ControlSort);
