import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Row, Col } from 'reactstrap';
import './WorkManagement.css';
import {Input} from 'reactstrap';
import { Table } from 'reactstrap';
import TaskListItem from './TaskListItem';
import {connect} from 'react-redux';
import * as actions from './../../../actions/index';


class TaskList extends Component {

    constructor(props) {
        super(props);
        this.state = {
            filterName: '',
            filterStatus: -1
        }
    }

    onChange = (event) => {
        var target = event.target;
        var name = target.name;
        var value = target.type === 'checkbox' ? target.checked : target.value;
        var filter = {
            name : name === 'filterName' ? value : this.state.filterName,
            status : name ==='filterStatus' ? value : this.state.filterStatus
        };
        //console.log(name);
        this.props.onFilterTable(filter);
        this.setState ({
            [name] : value
        });
       // console.log(this.state);
    }

    componentDidMount() {
        this.props.fetchAllItems();
    }

    render() {
        var {tasks, filterTable} = this.props; //var tasks = this.props.tasks

        if(filterTable.name) {
            tasks = tasks.filter((task) => {
                return task.name.toLowerCase().indexOf(filterTable.name.toLowerCase()) !== -1
            });
        }

        tasks = tasks.filter((task)=> {
            if(filterTable.status === -1) {
                return task;
            }else {
                return task.status === (filterTable.status === 1 ? true : false);
            }
        });

        var elmTasks = tasks.map((task, index) => {
           return <TaskListItem key={task.id} index={index} task={task} />
        });
        return (
            <Row className="mt-15">
                <Col>
                    <Table bordered hover>
                        <thead>
                            <tr>
                                <th>stt</th>
                                <th>Tên</th>
                                <th>Trạng thái</th>
                                <th>Hành động</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <th scope="row"></th>
                                <td>
                                    <Input onChange = {this.onChange} type="text" name="filterName" id="textId" />
                                </td>
                                <td>
                                    <Input onChange = {this.onChange} id = "mySelect" type="select" name="filterStatus">
                                        <option value = "0">Ẩn</option>
                                        <option value = "1">Kích hoạt</option>
                                        <option value = "-1">Tất cả</option>
                                    </Input>
                                </td>
                                <td></td>
                            </tr>
                            {elmTasks}
                        </tbody>
                    </Table>
                </Col>
            </Row>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        tasks : state.tasks,
        filterTable : state.filterTable
    }
}

const mapDispatchToProps = (dispatch, props) => {
    return {
        fetchAllItems : () => {
            dispatch(actions.listAllRequest());
        },
        onFilterTable : (filter) => {
            dispatch(actions.filterItem(filter));
        }
    }
}

export default  connect(mapStateToProps, mapDispatchToProps)(TaskList);
