import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col } from 'reactstrap';
import './WorkManagement.css';
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCoffee } from '@fortawesome/free-solid-svg-icons';
import { faAdd } from '@fortawesome/free-solid-svg-icons';



class WorkManagement extends Component {

    render() {
        return (
            <Container>
                <div className="text-center">
                    <h1>Quản lý công việc</h1>
                    <hr />
                </div>
                <Row>
                    <Col-4>
                        <div className="panel panel-warning">
                            <div className="panel-heading">
                                <h3 className="panel-title">thêm công việc</h3>
                                <Button className="panel-exit" color="danger">Thoát</Button>
                            </div>
                            <div className="panel-body">
                                <Form>
                                    <FormGroup>
                                        <Label for="exampleEmail">Tên công việc</Label>
                                        <Input type="text" name="name" id="name" placeholder="" />
                                    </FormGroup>
                                    <FormGroup>
                                        <Label for="exampleSelect">Trạng thái</Label>
                                        <Input type="select" name="select" id="exampleSelect">
                                            <option>hiện</option>
                                            <option>ẩn</option>

                                        </Input>
                                    </FormGroup>
                                    <div className="text-center">
                                        <Button color="success">lưu lại</Button> &nbsp;
                                        <Button color="danger">hủy bỏ</Button>
                                        <FontAwesomeIcon icon={faAdd} />
                                    </div>
                                </Form>
                            </div>
                        </div>
                    </Col-4>
                    <Col-8>

                    </Col-8>
                </Row>
            </Container>
        )
    }

}

export default WorkManagement;
