import React from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

import {
  BrowserRouter as Router,
  //Switch,
  Route,
  Switch,
  //Link
} from "react-router-dom";

import TopMenu from './components/TopMenu/TopMenu';
import routes from './routes';

const Home = () => <h2>Home</h2>;

function App() {
  return (

    <Router>
      <div className="App.js">
        <TopMenu />
        <Switch>
          {this.showContentMenus(routes)}
        </Switch>
        {/* <Route exact path="/">
          <Home />
        </Route>
        <Route path="/products">
          <Products />
        </Route>
        <Route path="/carts">
          <Carts />
        </Route>  
        <Route path="/products_management">
          <ProductManagement />
        </Route> */}
      </div>
    </Router>
  );

  showContentMenus = (routes) => {
    var result = null;
    if (routes.length > 0) {
      result = routes.map((route, index) => {
        result (
          <Route 
            key = {index}
            path = {route.path}
            exact = {route.exact}
            component = {route.main}
          />
        )
      })
    }
  }
}


export default App;
