import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col } from 'reactstrap';
import './WorkManagement.css';
import { Button} from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlusSquare } from '@fortawesome/free-solid-svg-icons';
import TaskForm from '../WorkManagement/components/TaskForm';
import Control from '../WorkManagement/components/Control';
import TaskList from '../WorkManagement/components/TaskList';
import axios from 'axios';

class WorkManagement extends Component {

    constructor(props) {
        super(props);
        this.state = {
            tasks : [],
            isDisplayForm : false
        }
    }

    componentDidMount(){
        axios.get('http://localhost:3001/workList').then(res => {
            this.setState({
                tasks: res.data
            });
        })
    }

    render() {
        const {tasks, isDisplayForm} = this.state;
        return (
            <Container>
                <div className="text-center">
                    <h1>Quản lý công việc</h1>
                    <hr />
                </div>
                <Row>
                    {
                        isDisplayForm === true ? <TaskForm /> : ''
                    }
                    <Col xs="8">
                        <Button color="primary" size="lg" disabled>
                            <FontAwesomeIcon icon={faPlusSquare} /> &nbsp;
                            Thêm công việc
                            </Button>
                        <Control />
                        <TaskList tasks = {tasks}/>
                    </Col>
                </Row>
            </Container>
        )
    }

}

export default WorkManagement;
