import * as types from './../constants/ActionTypes';

var initialState = [];

var myReducer  = (state = initialState, action) => {
    switch(action.type) {
        case types.LIST_ALL:
            state = action.items;
            return [...state];
        
        case types.ADD_ITEM:
            state.push(action.item);
            return [...state];

        
        default : return [...state];
    }
};

export default myReducer;
