import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Row, Col } from 'reactstrap';
import { Button } from 'reactstrap';
import './Home.css';

class SizeSetting extends Component {

    changeSize(value) {
        this.props.changeSize(value)
    }

    render() {
        return (
            <div>
                <Row className="setSize-head">
                    <Col>Size : {this.props.fontSize}</Col>
                </Row>
                <Row className="setSize-body">
                    <Col xs="2" >
                        <Button onClick={() => this.changeSize(+2)} color="success">Tăng</Button>
                    </Col>

                    <Col xs="2">
                        <Button onClick={() => this.changeSize(-2)} color="success">Giảm</Button>
                    </Col>
                </Row>
            </div>
        )
    }
}

export default SizeSetting;
