import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Col } from 'reactstrap';
import './WorkManagement.css';
import { Button, Form, FormGroup, Label, Input } from 'reactstrap';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

import {faTimes} from '@fortawesome/free-solid-svg-icons';


class TaskForm extends Component  {

    constructor(props) {
        super(props);
        this.state = {
            name: '' ,
            status : false
        }
    }

    render() {
        return (
            <Col xs="4">
                <div className="panel panel-warning">
                    <div className="panel-heading">
                        <h3 className="panel-title">Nhập công việc</h3>
                        <Button onClick = {this.props.onChangeDisplay} className="panel-exit" color="danger">Thoát</Button>
                    </div>
                    <div className="panel-body">
                        <Form>
                            <FormGroup>
                                <Label for="exampleEmail">Tên công việc :</Label>
                                <Input  type="text" 
                                        name="name" 
                                        id="name" 
                                        value = {this.state.name}
                                        onChange = {this.onChange}
                                         />
                            </FormGroup>
                            <FormGroup>
                                <Label for="exampleSelect">Trạng thái :</Label>
                                <Input type="select" name="select" id="exampleSelect">
                                    <option>hiện</option>
                                    <option>ẩn</option>
    
                                </Input>
                            </FormGroup>
                            <div className="text-center">
                                <Button color="success">lưu lại</Button> &nbsp;
                                        <Button color="danger">
                                    <FontAwesomeIcon className="" icon={faTimes} />
                                            Hủy bỏ
                                        </Button>
                            </div>
                        </Form>
                    </div>
                </div>
            </Col>
        )
    }


}

export default TaskForm;
