import React, { useState } from 'react';
import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
   
} from 'reactstrap';
import {
    Link
  } from "react-router-dom";

import {CartContext} from '../contexts/Cart'

const TopMenu = (props) => {
    const [isOpen, setIsOpen] = useState(false);

    const toggle = () => setIsOpen(!isOpen);

    return (
        <div>
            <Navbar color="light" light expand="md">
                <NavbarBrand href="/">reactstrap</NavbarBrand>
                <NavbarToggler onClick={toggle} />
                <Collapse isOpen={isOpen} navbar>
                    <Nav className="mr-auto" navbar>
                        <NavItem>
                            <NavLink  href="/">
                                Home
                            </NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink href="/products">
                                Products
                            </NavLink>
                        </NavItem>
                        <NavItem>
                            
                                <CartContext >
                               {({cartItems}) =>  <NavLink href="/work_management">Cart ({cartItems.length})</NavLink>}
                                </CartContext>
                            
                        </NavItem>
                        <NavItem>
                            <NavLink href="/work_management">
                                Quản lý công việc
                            </NavLink>
                        </NavItem>
                    </Nav>
                </Collapse>
            </Navbar>
        </div>
    );
}

export default TopMenu;
