import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import './WorkManagement.css';
import { Button} from 'reactstrap';
import {connect} from 'react-redux';
import {deleteItemRequest} from './../../../actions/index';
import {updateStatusRequest} from './../../../actions/index';
import {toggleForm} from './../../../actions/index';


class TaskListItem extends Component {

    onDelete = () => {
        this.props.deleteItem(this.props.task.id)
    };

    openForm = () => {
        if(!this.props.isDisplayForm) {
            this.props.onToggleForm()
        }
    };

    onUpdateStatus = () => {
        var item = {
            id : this.props.task.id,
            name : this.props.task.name,
            status : !this.props.task.status,
        }
        this.props.updateStatus(this.props.task.id, item)
    }

    render() {
        const {task, index} = this.props
        return (
            <tr>
                <th scope="row">{index}</th>
                <td>{task.name}</td>
                <td>
                     <Button onClick={this.onUpdateStatus} color={task.status === true ? 'success' : 'danger'} size="sm">{task.status === true ? 'kích hoạt' : 'ẩn'}</Button>
                </td>
                <td className="text-center">
                    <Button onClick = {this.openForm} color="success">Sửa</Button> &nbsp;
                    <Button onClick = {this.onDelete} color="danger">Xóa</Button>
                </td>
            </tr>
        )
    }

    
}

const mapStateToProps = state => {
    return{
        isDisplayForm :  state.isDisplayForm
    }
};


const mapDispatchToProps = (dispatch, props) => {
    return {
        deleteItem : (id) => {
            dispatch(deleteItemRequest(id))
        },
        updateStatus :(id, item) => {
            dispatch(updateStatusRequest(id, item))
        },
        onToggleForm : () => {
            dispatch(toggleForm());
        }
    }
}

export default connect(null,mapDispatchToProps)(TaskListItem);
