import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Col } from 'reactstrap';
import './WorkManagement.css';
import { Button, Form, FormGroup, Label, Input } from 'reactstrap';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

import {connect} from 'react-redux';
import * as actions from './../../../actions/index';


import {faTimes} from '@fortawesome/free-solid-svg-icons';

const uuidv4 = require('uuid/v4');

class TaskForm extends Component  {

    onChange = (event) => {
        var target = event.target;
        var name = target.name;
        var value = target.value;
        if(name === 'status') {
            value = target.value ==='true' ? true : false;
        }
        this.setState({
            [name] : value 
        });
    }

    onSubmit = (event) => {
        var id = uuidv4();
        var item = {
            id: id,
            name: this.state.name,
            status: this.state.status
        }
        event.preventDefault();
        this.props.onAddItem(item);
        //this.onClear();
    }

    onClear = () => {
        var emtyItem = {
            name: '' ,
            status : false
        }
        this.props.itemEditing = emtyItem;
    }

    render() {
        return (
            <Col xs="4">
                <div className="panel panel-warning">
                    <div className="panel-heading">
                        <h3 className="panel-title">Nhập công việc</h3>
                        <Button onClick = {this.props.onChangeDisplay} className="panel-exit" color="danger">Thoát</Button>
                    </div>
                    <div className="panel-body">
                        <Form onSubmit={this.onSubmit}>
                            <FormGroup>
                                <Label for="exampleEmail">Tên công việc :</Label>
                                <Input  type="text" 
                                        name="name" 
                                        value = {this.props.itemEditing.name}
                                        onChange = {this.onChange}
                                         />
                            </FormGroup>
                            <FormGroup>
                                <Label for="exampleSelect">Trạng thái :</Label>
                                <Input  type="select" 
                                        name="status" 
                                        value = {this.props.itemEditing.status}
                                        onChange = {this.onChange}
                                        >
                                    <option value={true}>hiện</option>
                                    <option value={false}>ẩn</option>
                                </Input>
                            </FormGroup>
                            <div className="text-center">
                                <Button type="submit" color="success">lưu lại</Button> &nbsp;
                                        <Button onClick={this.onClear} type="button" color="danger">
                                    <FontAwesomeIcon className="" icon={faTimes} />
                                            Hủy bỏ
                                        </Button>
                            </div>
                        </Form>
                    </div>
                </div>
            </Col>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        itemEditing : state.itemEditing
    }
}

const mapDispatchToProps = (dispatch, props) => {
    return {
        onAddItem : (item) => {
            dispatch(actions.addItemRequest(item));
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(TaskForm);
