import React, { Component } from "react";
import {
    Container, Row, Col, Card, CardImg, CardText, CardBody,
    CardTitle, Button
} from 'reactstrap';
import {connect} from 'react-redux';
//import * as actions from './../../actions/index';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCartPlus } from '@fortawesome/free-solid-svg-icons';

class ProductCartContainer extends Component {
    
    // componentDidMount() {
    //     this.props.fetchAllItems();
    // }

    render() {
        var {carts} = this.props;
        console.log(carts);

        var elmTasks = carts.map((cart, index) => {
            return  <Col sm="4">
                <Card>
                    <CardImg top width="100%"
                        src={cart.imageUrl}
                        alt="Card image cap" />
                    <CardBody>
                        <CardTitle>{cart.name}</CardTitle>
                        <CardText>{cart.price} $</CardText>
                        {cart.status ? <Button color="danger" > Xóa khỏi giỏ hàng <FontAwesomeIcon icon={faCartPlus} /> </Button> : <CardTitle>Hết hàng</CardTitle>}
                    </CardBody>
                </Card>
            </Col>
        });

        return (
            <Container>
                <Row className = "mtop-15">
                    <h2>Giỏ hàng</h2>
                </Row>
                <hr />
                <Row>
                    {elmTasks}
                </Row>
            </Container>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        carts : state.carts,
    }
}

const mapDispatchToProps = (dispatch, props) => {
    return {
    }
}

export default  connect(mapStateToProps, mapDispatchToProps)(ProductCartContainer);
