import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Col } from 'reactstrap';
import './WorkManagement.css';
import { UncontrolledDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
import {connect} from 'react-redux';
import * as actions from './../../../actions/index';


class ControlSort extends Component{

    onClick = (sortBy, sortValue) => {
        this.props.onSort({
            by : sortBy,
            value : sortValue
        });
    }

    render() {
        return (
            <Col xs="auto">
                <UncontrolledDropdown>
                    <DropdownToggle color="primary" caret>
                        Sắp xếp
                                </DropdownToggle>
                    <DropdownMenu>
                        <DropdownItem onClick = {() => this.onClick('name', 1)}>Tên A-Z</DropdownItem>
                        <DropdownItem onClick = {() => this.onClick('name', -1)}>Tên Z-A</DropdownItem>
                        <DropdownItem divider />
                        <DropdownItem onClick = {() => this.onClick('status', 1)}>Trạng thái kích hoạt</DropdownItem>
                        <DropdownItem onClick = {() => this.onClick('status', -1)}>Trạng thái ẩn</DropdownItem>
                    </DropdownMenu>
                </UncontrolledDropdown>
            </Col>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        sort : state.sort
    }
}

const mapDispatchToProps = (dispatch, props) => {
    return{
        onSort : (sort) => {
            dispatch(actions.sortItem(sort));
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ControlSort);
