import * as types from './../constants/ActionTypes';

 export const listAll = (items) => {
    return {
        type : types.LIST_ALL,
        items
    }
 };

 export const addItem = (item) => {
     return {
         type : types.ADD_ITEM,
         item //item:item
     }
 };
