import React, {  } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col } from 'reactstrap';
import './WorkManagement.css';
import { Button} from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlusSquare } from '@fortawesome/free-solid-svg-icons';
import TaskForm from '../WorkManagement/components/TaskForm';
import Control from '../WorkManagement/components/Control';
import TaskList from '../WorkManagement/components/TaskList';


const WorkManagement = (props) => {

    const [ setDropdownOpen] = useState(false);
    const toggle = () => setDropdownOpen(prevState => !prevState);

    return (
        <Container>
            <div className="text-center">
                <h1>Quản lý công việc</h1>
                <hr />
            </div>
            <Row>
                <TaskForm />
                <Col xs="8">
                    <Button color="primary" size="lg" disabled>
                        <FontAwesomeIcon icon={faPlusSquare} /> &nbsp;
                        Thêm công việc
                        </Button>
                    <Control />
                    <TaskList/>
                </Col>
            </Row>
        </Container>
    )

}

export default WorkManagement;
