import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import './WorkManagement.css';
import { Button} from 'reactstrap';
import {connect} from 'react-redux';
import {deleteItemRequest} from './../../../actions/index';


class TaskListItem extends Component {

    render() {
        const {task, index} = this.props
        return (
            <tr>
                <th scope="row">{index}</th>
                <td>{task.name}</td>
                <td>
                     <Button color={task.status === true ? 'success' : 'danger'} size="sm">{task.status === true ? 'kích hoạt' : 'ẩn'}</Button>
                </td>
                <td className="text-center">
                    <Button color="success">Sửa</Button> &nbsp;
                    <Button onClick = {this.props.deleteItem(task.id)} color="danger">Xóa</Button>
                </td>
            </tr>
        )
    }

    
}

const mapStateToProps = state => {
    return {

    }
}

const mapDispatchToProps = (dispatch, props) => {
    return {
        deleteItem : (id) => {
            dispatch(deleteItemRequest(id))
        }
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(TaskListItem);
