import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col } from 'reactstrap';

import ColorPicker from './ColorPicker'
import Reset from './Reset'
import Result from './Result';
import SizeSetting from './SizeSetting'

class Home extends Component {

    constructor(props) {
        super(props);
        this.state = {
            color : 'red',
            fontSize: 12
        };
        this.onSetColor = this.onSetColor.bind(this);
        this.oneChangeSize = this.oneChangeSize.bind(this);
        this.resetSize = this.resetSize.bind(this);
    }
    onSetColor (params) {
        this.setState ({
            color : params
        });
    }

    oneChangeSize (params) {
        this.setState ({
            fontSize : (this.state.fontSize + params >= 8 && this.state.fontSize + params <= 36) ? this.state.fontSize + params : this.state.fontSize
        });
        //console.log(params)
    }

    resetSize () {
        this.setState ( {
            fontSize : 12
        })
    }

    render() {
        return (
            
            <Container>
                <Row>
                    <ColorPicker color = {this.state.color} onReceiveColor = {this.onSetColor}/>
                    <Col className="setSize" xs="6">
                        <SizeSetting fontSize = {this.state.fontSize}  changeSize = {this.oneChangeSize}/>
                        <Reset resetSize = {this.resetSize}/>
                    </Col>
                </Row>
                <Result color = {this.state.color} fontSize = {this.state.fontSize}/>
            </Container>
        )   
    }
}

export default Home;
