import React, { Component } from "react";
import {
    Container, Row, Col, Button
} from 'reactstrap';
import * as actions from './../../actions/index';
import { Table } from 'reactstrap';
import { connect } from 'react-redux';
import './ProductCartContainer.css';
//import * as actions from './../../actions/index';


class ProductCartContainer extends Component {

    onDeleteCart = (id) => {
        this.props.deleteCart(id)
    };

    onDeleteAllCart = () => {
        this.props.deleteAllCart()
    };

    render() {
        var { carts } = this.props;
        var arr = [];
        
        for (var i = 0; i < length(carts); i++) {
            for (var j = i + 1; j < sl; j++)
            {
                var obj = {
                    item : carts[i],
                    sl : 1
                }
                
                if (carts[i].id === carts[j].id){
                    carts.splice(j, 1);
                    obj.sl = obj.sl + 1;
                }
                else {
                    arr.push(obj)
                }
            }
        }

        var elmCarts = <Col>
            <Table>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Hình ảnh</th>
                        <th>Sản phẩm</th>
                        <th>Giá</th>
                        <th>Số lượng</th>
                        <th>Xóa</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        arr.map((cart, index) => {
                            return <tr>
                                <th scope="row">{index}</th>
                                <td>
                                    <img src={cart.imageUrl} alt="Carts" />
                                </td>
                                <td>
                                    {cart.name}
                                </td>
                                <td>
                                    {cart.price}
                                </td>
                                <td>số lượng</td>
                                <td>
                                    <Button onClick={() => this.onDeleteCart(cart.id)} color="danger" > Xóa </Button>
                                </td>
                            </tr>
                        })
                    }
                </tbody>
            </Table>
        </Col>

        return (
            <Container>
                <Row className="mtop-15">
                    <Col>
                        <h2>Giỏ hàng ({carts.length} sản phẩm)</h2>
                        <h3>Thành tiền : 500 $</h3>
                    </Col>
                    <Col>
                        <Button color="primary">Tiến hành đặt hàng</Button> &nbsp;
                        <Button onClick={this.onDeleteAllCart} color="danger">Xóa tất cả</Button>
                    </Col>
                </Row>
                <hr />
                <Row>
                    {elmCarts}
                </Row>
            </Container>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        carts: state.carts,
    }
}

const mapDispatchToProps = (dispatch, props) => {
    return {
        deleteCart: (id) => {
            dispatch(actions.deleteCart(id))
        },
        deleteAllCart: () => {
            dispatch(actions.deleteAllCart())
        },
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ProductCartContainer);
