import * as types from './../constants/ActionTypes';
import axios from 'axios';

export const listAllRequest = () => {
    return(dispatch) => {
        axios.get('http://localhost:3001/workList').then(res => {
            dispatch(listAll(res.data))
        });
    }
};

 export const listAll = (items) => {
    return {
        type : types.LIST_ALL,
        items
    }
 };

export const addItemRequest = (item) => {
    return dispatch => {
        axios.post('http://localhost:3001/workList', item).then(res => {
            dispatch(addItem(res.data))
        });
    }
};

 export const addItem = (item) => {
     return {
         type : types.ADD_ITEM,
         item //item:item
     }
 };
