import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Col } from 'reactstrap';
import './WorkManagement.css';
import { Button, Form, FormGroup, Label, Input } from 'reactstrap';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

import {connect} from 'react-redux';
import * as actions from './../../../actions/index';


import {faTimes} from '@fortawesome/free-solid-svg-icons';

const uuidv4 = require('uuid/v4');

class TaskForm extends Component  {
    constructor(props) {
        super(props)
        this.state = {
            id : '',
            name : '',
            status : false
        }
    }

    componentWillMount() {
        if(this.props.itemEditing) {
            this.setState ({
                id :  this.props.itemEditing.id,
                name : this.props.itemEditing.name,
                status : this.props.itemEditing.status
            })
        }
    }

    componentWillReceiveProps(nextProps) {
        if(nextProps && nextProps.itemEditing) {
            this.setState({
                id :  nextProps.itemEditing.id,
                name : nextProps.itemEditing.name,
                status : nextProps.itemEditing.status
            })
        }
    }

    onChange = (event) => {
        var target = event.target;
        var name = target.name;
        var value = target.value;
        if(name === 'status') {
            value = target.value ==='true' ? true : false;
        }
        this.setState({
            [name] : value 
        });
    };

    clearItem = () => {
        this.props.onClearItem()
    };

    onSubmit = (event) => {
        var id = uuidv4();
        var item = {
            id: id,
            name: this.state.name,
            status: this.state.status
        }
        event.preventDefault();
        this.props.onAddItem(item);
        this.props.onClearItem();
    };

    render() {
        var state = this.state;
        return (
            <Col xs="4">
                <div className="panel panel-warning">
                    <div className="panel-heading">
                        <h3 className="panel-title">Thêm công việc</h3>
                        <Button onClick = {this.props.onChangeDisplay} className="panel-exit" color="danger">Thoát</Button>
                    </div>
                    <div className="panel-body">
                        <Form onSubmit={this.onSubmit}>
                            <FormGroup>
                                <Label for="exampleEmail">Tên công việc :</Label>
                                <Input  type="text" 
                                        name="name" 
                                        value = {state.name}
                                        onChange = {this.onChange}
                                         />
                            </FormGroup>
                            <FormGroup>
                                <Label for="exampleSelect">Trạng thái :</Label>
                                <Input  type="select" 
                                        name="status" 
                                        value = {state.status}
                                        onChange = {this.onChange}
                                        >
                                    <option value={true}>hiện</option>
                                    <option value={false}>ẩn</option>
                                </Input>
                            </FormGroup>
                            <div className="text-center">
                                <Button type="submit" color="success">lưu lại</Button> &nbsp;
                                        <Button onClick={this.clearItem} type="button" color="danger">
                                    <FontAwesomeIcon className="" icon={faTimes} />
                                            Hủy bỏ
                                        </Button>
                            </div>
                        </Form>
                    </div>
                </div>
            </Col>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        itemEditing : state.itemEditing
    }
};

const mapDispatchToProps = (dispatch, props) => {
    return {
        onAddItem : (item) => {
            dispatch(actions.addItemRequest(item));
        },
        onClearItem : () => {
            dispatch(actions.onClear());
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(TaskForm);
